import React,{useEffect, useState} from 'react'

const TempApp = () => {

    const [city, setcity] = useState("Vadodara");
    const [search, setsearch] = useState("vadodara");

   useEffect(() => {
       const fetchApi = async () => {
           const url=`http://api.openweathermap.org/data/2.5/weather?q=${search}&appid=19ab4e6cebbc2b2d7ac576da57bd4562`
           const response = await fetch(url);
        //    console.log(response);
           const resJson = await response.json();
        //    console.log(resJson);
           setcity(resJson);
       }
       fetchApi();

       
   })

    const inputEvent = (event) => {
        const data = event.target.value;
        setsearch(data);
    }
    return (
        <>
        <div className="center box">
            <div className="inputdata">
                <input
                    type="search"
                    className="inputField"
                    placeholder="Search City.."
                    name=""
                    value={search}
                    onChange={inputEvent}
                    />
                <div className="info">
                 <h2 className="location">
                    <i className="fas fa-street-view"></i>
                    {search}
                  {city.name}
                 </h2>
                 <h1 className="temp">temperature </h1>
                 <h3 className="temp_max">Max and min temp</h3>
               </div>
             
            </div>
            </div>
            
       </>
    )
}

export default TempApp;
